exports.getCollection = function (req,collName){
    collName = !collName ? 'shoppinglists' : collName;
    return req.mongoDb.get(collName);
};

exports.updateModifiedDateInDBCollection = function(collection, listId, successCallback, failCallback){
    collection.update(
        {"_id": listId},
        { $set: {"modified": new Date()} },
        function (err, numOfModifiedDocs) {
            console.log(numOfModifiedDocs);
            console.log("ERROR: " + err);
            if (!err && numOfModifiedDocs>0) {
                successCallback();
            } else {
                if (failCallback){//not required
                    failCallback();
                }
            }
        }
    );
};

exports.dateFormatGerman = "D.M.YYYY HH:mm:ss";


